    @Override
    public void executeWithNewKieSession(String businessDomainPartyNumber, KieSessionCallback callback) {
        KieSession kieSession = null;
        try {
            kieSession = kieContainers.get(businessDomainPartyNumber).newKieSession();
            callback.doInKieSession(kieSession);
        } finally {
            if (kieSession != null) {
                kieSession.dispose();
            }
        }
    }

    @Override
    public void buildAndRefresh() {
        refresh(true);
    }

