    private String getNextKieModuleVersion(String version) {
        String kieModuleVersion = "1.0.0";
        if (StringUtils.equals(version, kieModuleVersion)) {
            //
        } else {
            String existingVersion = version;
            int lastIndex = existingVersion.lastIndexOf('.') + 1;
            kieModuleVersion = existingVersion.substring(0, lastIndex) + (Integer.parseInt(existingVersion.substring(lastIndex)) + 1);
        }
        return kieModuleVersion;
    }

